mapper.select:
SELECT DISTINCT s_role.id, s_role.NAME, s_role.description, s_role.`status` FROM sys_role s_role WHERE s_role.STATUS = 1
AND s_role.id not in
#{id}

SELECT
ID,RES_URL resUrl,PARENT_ID parentId,ICON,LEVEL,
RES_KEY resKey,DESCRIPTION,NAME,PERMISSION,TYPE,
status
FROM SYS_RESOURCE


		ID = #{id}


		RES_URL = #{resUrl}


		PARENT_ID = #{parentId}


		ICON = #{icon}


		LEVEL = #{level}


		RES_KEY = #{resKey}


		DESCRIPTION = #{description}


		NAME like "%"#{name}"%"


		PERMISSION = #{permission}


		TYPE = #{type}


		status = #{status}
